import React from 'react';
import './App.css';
import {Header} from "./components/Header/Header";
import {Navbar} from "./components/Navbar/Navbar";
import {Profile} from "./components/Profile/Profile";
import {Dialogs} from "./components/Dialogs/Dialogs";
import {BrowserRouter, Route} from "react-router-dom";
import {News} from "./components/News/News";
import {Music} from "./components/Music/Music";
import {Settings} from "./components/Settings/Settings";
import {RootStateType, StoreType} from '../src/redux/state'

export type AppStateType = {
    store: StoreType
    //state: RootStateType
    addPostCallback: (postMessage: string) => void
    updateNewPostText: (newTextValue: string) => void
}

function App(props: AppStateType) {
    const state = props.store.getState;

    return (
        <BrowserRouter>
            <div className='app-wrapper'>
                <Header/>
                <Navbar/>
                <div className='app-wrapper-content'>
                    <Route path={'/dialogs'}
                           render={() => <Dialogs dialogsData={state.dialogsPage.dialogsData} messageData={props.state.dialogsPage.messageData}/>}/>
                    <Route path={'/profile'} render={() => <Profile
                        postData={props.store.state.profilePage.postData}
                        addPostCallback={props.addPostCallback}
                        newTextValue={props.store.profilePage.newTextValue}
                        updateNewPostText={props.updateNewPostText}
                    />}/>
                    <Route path={'/news'} component={News}/>
                    <Route path={'/music'} component={Music}/>
                    <Route path={'/settings'} component={Settings}/>
                </div>
            </div>
        </BrowserRouter>
    );
}

export default App;
