import React from 'react';
import s from './MyPosts.module.css';
import {Post} from "./Post/Post";
import {PostData, ProfilePageType} from "../../../redux/state";

type MyPostPropsType = {
    postData: PostData[]
    addPostCallback: (postMessage: string) => void
    newTextValue: string
}


export const MyPosts = (props: MyPostPropsType) => {

    let getPostData = props.postData.map((ev) => {
        return (
            <Post name={ev.name} message={ev.message} likes={ev.likes}/>
        )
    })

    let newPostElement = React.createRef<HTMLTextAreaElement>();

    const addPost = () => {
        let text = newPostElement.current;

        if (text) {
            props.addPostCallback(text.value)
            text.value = '';
        }
    }

    const onChangeText = () => {
        let text = newPostElement.current;
        console.log(text)
    }
    return (
        <div className={s.posts}>
            <h3>My posts</h3>
            <div>
                <div>
                    <textarea onChange={onChangeText} ref={newPostElement}></textarea>
                </div>
                <div>
                    <button onClick={addPost}>Add post</button>
                </div>
            </div>
            {getPostData}
        </div>
    );
};
