import React from 'react';
import {UsersPagePropsType} from "./UsersContainer";
import styles from "./Users.module.css"
import axios from "axios";
import avatar from '../../assets/images/avatar.png'

export class Users extends React.Component<UsersPagePropsType> {

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {
            this.props.setUsers(response.data.items);
        });
    }

    onPageChanged = (pageNumber: number) => {
        this.props.setCurrentPage(pageNumber);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`).then(response => {
            this.props.setUsers(response.data.items);
            this.props.setCurrentPage(response.data.totalCount)
        });
    }

    render() {
        let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize)

        let pages = [];

        for (let i = 1; i <= pagesCount; i++) {
            pages.push(i);
        }
        return <div>
            <div>
                {pages.map(p => {
                    return <span className={this.props.currentPage === p ? styles.selectedPage : 'default'}
                                 onClick={() => {
                                     this.onPageChanged(p)
                                 }}>{p}</span>
                })
                }
            </div>
            USERS
            {this.props.usersPage.map(u =>
                <div key={u.id}>
                    <div>
                        <img src={u.avatar != null ? u.avatar : avatar} alt="picture" className={styles.avatar}/>
                    </div>

                    <div>
                        {u.followed
                            ? <button onClick={() => {
                                this.props.follow(u.id)
                            }}>Follow</button>
                            : <button onClick={() => {
                                this.props.unfollow(u.id)
                            }}>Unfollow</button>}
                    </div>
                    <div>
                        {u.name}
                    </div>
                </div>
            )
            }
        </div>
    }

};
