import './index.css';
import React from 'react';
import ReactDOM from "react-dom";
import App from "./App";
import {addPost, RootStateType, state, store, updateNewPostText} from "./redux/state";


export const render = () => {
    ReactDOM.render(
        <App state={store.getState()} addPostCallback={addPost} updateNewPostText={updateNewPostText}/>,
        document.getElementById('root')
    );
};

render();

store.subscribe(render);
