import React from 'react';
import {UsersPagePropsType} from "./UsersContainer";
import styles from "./Users.module.css"
import axios from "axios";
import avatar from '../../assets/images/avatar.png'

export const Users = (state: UsersPagePropsType) => {

    if (state.usersPage.users.length === 0) {
        axios
            .get('https://social-network.samuraijs.com/api/1.0/users')
            .then(response => {
                state.setUsers(response.data.items);
            });
    }
    return (
        <div>
            USERS
            {state.usersPage.users.map(u =>
                <div key={u.id}>
                    <div>
                        <img src={u.avatar != null ? u.avatar : avatar} alt="picture" className={styles.avatar}/>
                    </div>

                    <div>
                        {u.followed
                            ? <button onClick={() => {
                                state.follow(u.id)
                            }}>Follow</button>
                            : <button onClick={() => {
                                state.unfollow(u.id)
                            }}>Unfollow</button>}
                    </div>
                    <div>
                        {u.name}
                    </div>
                </div>
            )
            }
        </div>
    );
};
