import React from 'react';
import {UsersPagePropsType} from "./UsersContainer";
import styles from "./Users.module.css"

const Users = (state: UsersPagePropsType) => {
    console.log(state.usersPage.users)
    debugger
    return (
        <div>
            USERS
            {state.usersPage.users.map(u =>
                <div key={u.id}>
                    <div>
                        <img src={u.avatar} alt="picture" className={styles.avatar}/>
                    </div>
                    <div>
                        {u.followed
                            ? <button onClick={() => {state.follow(u.id)}}>Follow</button>
                            : <button onClick={() => {state.unfollow(u.id)}}>Unfollow</button>}
                    </div>
                    <div>
                        {u.name}
                    </div>
                </div>
                )
            }
        </div>
    );
};

export default Users;
