import React from 'react';
import {AppStateType} from "../../redux/redux-store";
import {RouteComponentProps, withRouter} from "react-router-dom";
import {connect} from "react-redux";
import {setUserData, UserDataType} from "../../redux/authReducer";
import {Header} from "./Header";
import axios from "axios";

type ParamsType = {
    userId: string,
    email: string,
    login: string
}

type MapStatePropsType = {
    data: UserDataType
}

type MapDispatchPropsType = {
    setUserData: (userId: string, email: string, login: string) => void
}

export type HeaderPropsType = RouteComponentProps<ParamsType> & MapStatePropsType & MapDispatchPropsType

export class HeaderContainerAPI extends React.Component<HeaderPropsType>{
    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/auth/me`, {
            withCredentials: true
        } )
            .then(response => {
                if(response.data.resultCode === 0){
                    this.props.setUserData(response.data.data.userId, response.data.data.email, response.data.data.login)
                }
            });
    }

    render() {
        return (
            <Header {...this.props}/>
        );
    }
};


const mapStateToProps = (state: AppStateType): MapStatePropsType => {
    return {
        data: state.auth
    }
}

let WithURLDataContainer = withRouter(HeaderContainerAPI)

export const HeaderContainer = connect(mapStateToProps, {
    setUserData
})(WithURLDataContainer)
