import React from 'react';

export type UsersReducersActionType =
    | ReturnType<typeof Follow>
    | ReturnType<typeof Unfollow>
    | ReturnType<typeof SetUsers>

const FOLLOW = "FOLLOW";
const UNFOLLOW = "UNFOLLOW";
const SET_USERS = "SET_USERS";


export type UserPropsType = {
    users: Array<UsersData>
}

type UsersData = {
    id: number,
    name: string
    followed: boolean
    avatar: string
}

const initialState = {
    users: [
        {
            id: 1,
            name: 'Eduarda',
            followed: false,
            avatar: 'https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg'
        },
        {
            id: 2,
            name: 'Artiom',
            followed: true,
            avatar: 'https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg'
        },
        {
            id: 3,
            name: 'Dasha',
            followed: false,
            avatar: 'https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg'
        },
        {
            id: 4,
            name: 'Ivan',
            followed: true,
            avatar: 'https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg'
        },
        {
            id: 5,
            name: 'Ekaterina',
            followed: true,
            avatar: 'https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg'
        },
    ]
}

//export type InitialStateType = typeof initialState

export const usersReducer = (state: UserPropsType = initialState, action: UsersReducersActionType) => {

    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userID) {
                        return {...state, followed: false}
                    }
                })
            }
        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userID) {
                        return {...state, followed: true}
                    }
                })
            }

        case SET_USERS:
            return {
                ...state,
                users: [...state.users, ...action.users]
            }
        default:
            return state;
    }
};

export const Follow = (userID: number) => ({
        type: FOLLOW,
        userID: userID
    } as const
)
export const Unfollow = (userID: number) => ({
        type: UNFOLLOW,
        userID: userID
    } as const
)

export const SetUsers = (users: Array<UsersData>) => ({
        type: SET_USERS,
        users: users
    } as const
)
