import React from 'react';
import {UsersPagePropsType} from "./UsersContainer";
import styles from "./Users.module.css"

export const Users = (state: UsersPagePropsType) => {

    if(state.usersPage.users.length === 0) {
        state.setUsers([
            {
                id: 1,
                name: 'Eduarda',
                followed: false,
                avatar: 'https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg'
            },
            {
                id: 2,
                name: 'Artiom',
                followed: true,
                avatar: 'https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg'
            },
            {
                id: 3,
                name: 'Dasha',
                followed: false,
                avatar: 'https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg'
            },
            {
                id: 4,
                name: 'Ivan',
                followed: true,
                avatar: 'https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg'
            },
            {
                id: 5,
                name: 'Ekaterina',
                followed: true,
                avatar: 'https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg'
            }
        ])
    }
    return (
        <div>
            USERS
            {state.usersPage.users.map(u =>
                <div key={u.id}>
                    <div>
                        <img src={u.avatar} alt="picture" className={styles.avatar}/>
                    </div>

                    <div>
                        {u.followed
                            ? <button onClick={() => {
                                state.follow(u.id)
                            }}>Follow</button>
                            : <button onClick={() => {
                                state.unfollow(u.id)
                            }}>Unfollow</button>}
                    </div>
                    <div>
                        {u.name}
                    </div>
                </div>
            )
            }
        </div>
    );
};
