import React, {ChangeEvent} from 'react';
import s from './MyPosts.module.css';
import {Post} from "./Post/Post";
import {ActionsType, PostData, StoreType} from "../../../redux/store";
import {AddPostAC, NewPostText} from "../../../redux/profileReducer";

type MyPostPropsType = {
    // postData: PostData[]
    store: StoreType
    addPostCallback: (postMessage: string) => void
    updateNewPostText: (newTextValue: string) => void
}


export const MyPosts = (props: MyPostPropsType) => {
    let state = props.store.getState();

    let getPostData =  state.profilePage.postData.map((ev) => {
        return (
            <Post name={ev.name} message={ev.message} likes={ev.likes}/>
        )
    })


    const addPost = () => {
        props.addPostCallback(state.profilePage.newTextValue);
    }

    const onChangeText = (e: ChangeEvent<HTMLTextAreaElement>) => {
        props.updateNewPostText(e.currentTarget.value)
    }
    return (
        <div className={s.posts}>
            <h3>My posts</h3>
            <div>
                <div>
                    <textarea onChange={onChangeText} value={state.profilePage.newTextValue}/>
                </div>
                <div>
                    <button onClick={addPost}>Add post</button>
                </div>
            </div>
            {getPostData}
        </div>
    );
};
