import React, {ChangeEvent} from 'react';
import s from './MyPosts.module.css';
import {Post} from "./Post/Post";
import {ActionsType, PostData, StoreType} from "../../../redux/store";
import {AddPostAC, NewPostText} from "../../../redux/profileReducer";
import {MyPosts} from "./MyPosts";

type MyPostPropsType = {
    // postData: PostData[]
    // newTextValue: string
    // dispatch: (action: ActionsType) => void
    store: StoreType
}


export const MyPostsContainer = (props: MyPostPropsType) => {
    let state = props.store.getState();

    const addPost = () => {
        props.store.dispatch(AddPostAC(state.profilePage.newTextValue));

    }

    const onChangeText = (text: string) => {
        props.store.dispatch(NewPostText(text));
    }
    return (
        <div className={s.posts}>
            <MyPosts addPostCallback={addPost}
                     updateNewPostText={onChangeText}
                     store={props.store}/>
        </div>
    );
};
