import React, {ChangeEvent} from 'react';
import s from './Dialogs.module.css'
import {NavLink} from "react-router-dom";
import {DialogItem} from "./DialogItem/DialogItem";
import {Message} from "./Message/Message";
import {
    ActionsType,
    DialogsData, DialogsPageType,
    MessageData, StoreType,
} from "../../redux/store";
import {AddMessage, NewMessage} from "../../redux/dialogsReducer";

type DialogsType = {
    store: StoreType
    addMessage: () => void
    onChangeMessage: (newMessage: string) => void
}

export const Dialogs = (props: DialogsType) => {
    let state = props.store.getState();

    const dialog = state.dialogsPage.dialogsData.map((ev) => {
        return (
            <DialogItem id={ev.id} name={ev.name}/>
        )
    })
    const message = state.dialogsPage.messageData.map((ev) => {
        return (
            <Message id={ev.id} message={ev.message}/>
        )
    })

    const addMessage = () => {
        //props.dispatch(AddMessage(props.newMessage));
        props.addMessage();
    }

    const onChangeMessage = (e: ChangeEvent<HTMLTextAreaElement>) => {
        props.onChangeMessage(e.currentTarget.value)

    }

    return (

        <div className={s.dialogs}>
            <div className={s.dialogItem}>
                {dialog}
            </div>

            <div className={s.messages}>
                <div>{message}</div>
                <div>
                    <div className={s.sendMessage}>
                        <textarea onChange={onChangeMessage}
                                  value={state.dialogsPage.newMessage}
                                  placeholder={'Enter your message'}></textarea>
                    </div>
                    <div>
                        <button onClick={addMessage}>Send</button>
                    </div>
                </div>
            </div>

        </div>
    );
};
