import React from 'react';
import {ActionsType, AddMessageActionType, DialogsPageType, MessageData, NewMessageActionType} from "./store";

const ADD_MESSAGE = "ADD-MESSAGE";
const NEW_MESSAGE = "NEW-MESSAGE";

export const dialogsReducer = (state: DialogsPageType, action: ActionsType) => {
    switch (action.type) {
        case ADD_MESSAGE:
            const newMessage: MessageData = {
                message: action.postMessage,
                id: 10
            }
            state.messageData.push(newMessage)
            state.newMessage = '';
            return state;
        case NEW_MESSAGE:
            state.newMessage = action.newText;
            return state;
        default:
            return state;
    }
};

export const AddMessage = (newText: string): AddMessageActionType => ({
    type: ADD_MESSAGE,
    postMessage: newText
})
export const NewMessage = (newText: string): NewMessageActionType => ({
    type: NEW_MESSAGE,
    newText: newText
})
